import React, {useEffect} from 'react' 
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Drawer from '@material-ui/core/Drawer';
import Box from '@material-ui/core/Box';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import Badge from '@material-ui/core/Badge';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Link from '@material-ui/core/Link';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import NotificationsIcon from '@mui/icons-material/Notifications';
import { mainListItems, secondaryListItems } from './listItems';
import axios  from 'axios';
/* import Chart from './Chart'; */
/*import Deposits from './Deposits';*/
/* import Orders from './Orders'; */
import UpdateFile from './UpdateFile';
import { useParams, useHistory } from 'react-router-dom';
import { useState } from 'react';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://mui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  toolbar: {
    paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: '0 8px',
    ...theme.mixins.toolbar,
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  menuButtonHidden: {
    display: 'none',
  },
  title: {
    flexGrow: 1,
  },
  drawerPaper: {
    position: 'relative',
    whiteSpace: 'nowrap',
    width: drawerWidth,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerPaperClose: {
    overflowX: 'hidden',
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing(9),
    },
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    flexGrow: 1,
    height: '100vh',
    overflow: 'auto',
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  paper: {
    padding: theme.spacing(2),
    display: 'flex',
    overflow: 'auto',
    flexDirection: 'column',
  },
  fixedHeight: {
    height: 240,
  },
}));

export default function Dashboard() {

    const { id } = useParams();
    const history = useHistory();
    const [identification, setIdentification] = useState();
    const [names, setNames] = useState();
    const [lastName, setLastName] = useState();
    const [description, setDescription] = useState();

    useEffect(() => {
        axios.get(`http://localhost:3001/getFile/${id}`)
            .then(result => {console.log(result)
                setIdentification(result.data.identification);
                setNames(result.data.names);
                setLastName(result.data.lastName);
                setDescription(result.data.description);


            })
            .catch(error => console.error(error));

    }, [id]);


    const Update = (e) => {
        e.preventDefault();
        axios.put(`http://localhost:3001/updateFile/${id}`, { identification, names, lastName, description })
            .then(result => {
                history.push('/Admin/DashboardUpdateFile');
                // Manejar la respuesta o redirigir a una página de éxito
                console.log(result);
                // Puedes redirigir a una página de éxito o a donde necesites
                
            })
            .catch(error => console.error(error));
    };

  axios.defaults.withCredentials = true;
  
  const classes = useStyles();
  const [open, setOpen] = React.useState(true);
  const handleDrawerOpen = () => {
    setOpen(true);
  };
  const handleDrawerClose = () => {
    setOpen(false);
  };
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);


   const[suc,setSuc]= useState()
    
  useEffect(()=>   {
      axios.get('http://localhost:3001/dashboard')
      .then(result => {console.log(result)
         if(result.data === "Success"){
  
          setSuc("Successded OK") 
          
         }else{
                    history.push('/home')

         }
         
     
      
      })
      .catch(err=> console.log(err))
     
  },[])
  return (
    <div className={classes.root}>
      
      <CssBaseline />
      
      <AppBar position="absolute" className={clsx(classes.appBar, open && classes.appBarShift)}>
      
        <Toolbar className={classes.toolbar}>
          <IconButton
            edge="start"
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            className={clsx(classes.menuButton, open && classes.menuButtonHidden)}
          >
            <MenuIcon />
          </IconButton>
          <Typography component="h1" variant="h6" color="inherit" noWrap className={classes.title}>
          <Link color="white" href='/'>Clinica Dental Sofia Castro R</Link>
          </Typography>
          <IconButton color="inherit">
            <Badge badgeContent={4} color="secondary">
              <NotificationsIcon />
            </Badge>
          </IconButton>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="permanent"
        classes={{
          paper: clsx(classes.drawerPaper, !open && classes.drawerPaperClose),
        }}
        open={open}
      >
        <div className={classes.toolbarIcon}>
          <IconButton onClick={handleDrawerClose}>
            <ChevronLeftIcon />
          </IconButton>
        </div>
        <Divider />
        <List>{mainListItems}</List>
        <Divider />
        <List>{secondaryListItems}</List>
      </Drawer>
      <main className={classes.content}>
        <div className={classes.appBarSpacer} />
        <Container maxWidth="lg" className={classes.container}>
          <Grid container spacing={3}>
            {/* <Grid item xs={12} md={8} lg={9}>
              <Paper className={fixedHeightPaper}>
                <Chart />
              </Paper>
            </Grid> */}
            
            {/* <Grid item xs={12} md={4} lg={3}>
              <Paper className={fixedHeightPaper}>
                <Deposits />
              </Paper>
            </Grid> */}
            
            {/* Recent Orders */}
            <Grid item xs={12}>
              <Paper className={classes.paper}>
              <div>
            
            <form onSubmit={Update}>
            <h1>Update File</h1>
                <div>
                    <label>Identification:</label>
                    <input type="text" name="identification" value={identification} onChange={(e) => setIdentification(e.target.value)} />
                </div>
                <div>
                    <label>Name:</label>
                    <input type="text" name="name" value={names} onChange={(e) => setNames(e.target.value)} />
                </div>
                <div>
                    <label>LastName:</label>
                    <input type="text" name="lastName" value={lastName} onChange={(e) => setLastName(e.target.value)} />
                </div>
                <div>
                    <label>Description:</label>
                    <input type="text" name="description" value={description} onChange={(e) => setDescription(e.target.value)} />
                </div>

                <button className='btn btn-success'>Update File</button>
            </form>
        </div>
              </Paper>
            </Grid>
          </Grid>
          <Box pt={22}>
            <Copyright />
          </Box>
        </Container>
      </main>
    </div>
  );
}
