import React from 'react';
import { useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { useHistory } from 'react-router-dom';
import axios from 'axios';


const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(3),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default function SignUp() {

  const classes = useStyles();
  const history = useHistory();

  // Estado para los nuevos atributos
  const [name, setName] = useState('');
  const [lastname, setLastname] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
  const [identification, setIdentification] = useState('');
  const [phone, setPhone] = useState('');
  const [birthday, setBirthday] = useState('');
  const [gender, setGender] = useState('');
  const [province, setProvince] = useState('');
  const [canton, setCanton] = useState('');
  const [distrit, setDistrit] = useState('');

  const handleChange = (e) => {
    const {name, value} = e.target;
    setFormData({
      ...formData, [name] : value
    })
  };


  const handleSubmit = (e) => {


    e.preventDefault()
    axios.post('http://localhost:3001/register' , {name,lastname, email, password,identification,phone,birthday,gender,province,canton,distrit})
    .then(result => {console.log(result)

      history.push('/SignIn/signin')

      const validationErrors = {}
      if(!formData.name.trim()){

      }


    })
    .catch(err=> console.log(err))


    // Aquí puedes implementar la lógica para procesar el inicio de sesión
    // Puedes enviar los datos (nombre, correo, contrasena) a tu servidor para autenticación.
};

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}></Avatar>
        <Typography component="h1" variant="h5">
          Registrarse
        </Typography>
        <form onSubmit={handleSubmit} className={classes.form} noValidate>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <TextField
                autoComplete="fname"
                name="firstName"
                variant="outlined"
                required
                fullWidth
                id="firstName"
                label="Primer Nombre"
                onChange={(handleChange) => setName(e.target.value)}
                autoFocus
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="lastName"
                label="Apellido"
                name="lastName"
                onChange={(handleChange) => setLastname(e.target.value)}
                autoComplete="lname"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                id="email"
                label="Correo"
                name="email"
                onChange={(handleChange) => setEmail(e.target.value)}
                autoComplete="email"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                name="password"
                label="Contraseña"
                type="password"
                id="password"
                onChange={(handleChange) => setPassword(e.target.value)}
                autoComplete="current-password"

              />
            </Grid>
            {/* Nuevos campos */}
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                fullWidth
                id="identification"
                label="Número de Identificación"
                onChange={(handleChange) => setIdentification(e.target.value)}
                name="identification"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                fullWidth
                id="phone"
                label="Número de Teléfono"
                onChange={(handleChange) => setPhone(e.target.value)}
                name="phone"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                type = "date"
                variant="outlined"
                fullWidth
                id="birthday"
                label="Fecha de Nacimiento"
                onChange={(handleChange) => setBirthday(e.target.value)}
                name="birthday"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                fullWidth
                id="gender"
                label="Género"
                onChange={(handleChange) => setGender(e.target.value)}
                name="gender"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                fullWidth
                id="province"
                label="Provincia"
                onChange={(handleChange) => setProvince(e.target.value)}
                name="province"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                fullWidth
                id="canton"
                label="Cantón"
                onChange={(handleChange) => setCanton(e.target.value)}
                name="canton"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                fullWidth
                id="distrit"
                label="Distrito"
                onChange={(handleChange) => setDistrit(e.target.value)}
                name="distrit"
              />
            </Grid>
            {/* Fin de los nuevos campos */}
            <Grid item xs={12}>
              <FormControlLabel
                control={<Checkbox value="allowExtraEmails" color="primary" />}
                label="Me gustaría recibir promociones por correo."
              />
            </Grid>
          </Grid>
          <Button
            onClick={handleSubmit}
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Registrarse
          </Button>
          <Grid container justifyContent="flex-end">
            <Grid item>
              <Link href="/SignIn/signIn" variant="body2">
                ¿Ya tiene una cuenta? Inicia Sesión
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
      <Box mt={6}></Box>
    </Container>
  );
}
