import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import axios from 'axios';

function UpdateFile() {
  const { id } = useParams();
  const history = useHistory();
  const [userData, setUserData] = useState({});
  
  useEffect(() => {
    axios.get(`http://localhost:3001/getFile/${id}`)
      .then((response) => {
        setUserData(response.data);
        

      })
      .catch((error) => {
        console.error(error);
      });
  }, [id]);
  
  const handleUpdate = () => {
    // Envía una solicitud para actualizar los datos del usuario en el servidor
    axios.put(`http://localhost:3001/updateFile/${id}`, userData)
      .then((response) => {
        history.push('/'); // Redirige de vuelta a la lista de usuarios
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const handleInputChange = (e) => {
    const { names, value } = e.target;
    setUserData({ ...userData, [names]: value });
  };

  return (
    <div>
      <h1>Update User</h1>
      <form>
      <div>
          <label>Identification:</label>
          <input type="text" name="identification" value={userData.identification} onChange={handleInputChange} />
        </div>
        <div>
          <label>Name:</label>
          <input type="text" name="name" value={userData.names} onChange={handleInputChange} />
        </div>
        <div>
          <label>Last Name:</label>
          <input type="text" name="lastName" value={userData.lastName} onChange={handleInputChange} />
        </div>
        <div>
          <label>Phone:</label>
          <input type="text" name="phone" value={userData.phone} onChange={handleInputChange} />
        </div>
        
        
        <button onClick={handleUpdate}>Update User</button>
      </form>
    </div>
  );
}

export default UpdateFile;
