import React from 'react';
import { useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import Textfield from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';

import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
/* import styles from "./styles.module.css"; */
import Swal from 'sweetalert2';
import 'sweetalert2/dist/sweetalert2.min.css';
import {TextField, FormControl, InputLabel, Select, MenuItem } from '@mui/material';

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(3),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));
//-------------------------------------------------------------------------------------------------------------------------------------------
const provinces = ['San Jose', 'Alajuela', 'Cartago', 'Heredia', 'Guanacaste', 'Puntarenas', 'Limón'];

const cantonsByProvince = {
  'San Jose': ['San Jose', 'Escazu', 'Desamparados'], // Ejemplo de cantones en San Jose
  'Alajuela': ['Alajuela', 'Grecia', 'San Ramon'],     // Ejemplo de cantones en Alajuela
  // ... Define los cantones para las otras provincias
};

const districtsByCanton = {
  'San Jose': {
    'San Jose': ['Carmen', 'Merced', 'Catedral'], // Ejemplo de distritos en San Jose, San Jose
    'Escazu': ['Escazu Centro', 'San Rafael', 'San Antonio'], // Ejemplo de distritos en Escazu
    // ... Define los distritos para los cantones de San Jose
  },
  'Alajuela': {
    'Alajuela': ['Alajuela Centro', 'San Jose', 'Atenas'], // Ejemplo de distritos en Alajuela, Alajuela
    'Grecia': ['Grecia Centro', 'San Isidro', 'San Roque'], // Ejemplo de distritos en Grecia
    // ... Define los distritos para los cantones de Alajuela
  },
  // ... Define los distritos para las provincias y cantones restantes
};


//---------------------------------------------------------------------------------------------------------------------------------------------





export default function SignUp() {
  const [province, setProvince] = useState('');
  const [canton, setCanton] = useState('');
  const [district, setDistrict] = useState('');

  const handleProvinceChange = (e) => {
    const selectedProvince = e.target.value;
    setProvince(selectedProvince);
    // Reiniciar los valores de canton y distrito cuando cambie la provincia
    setCanton('');
    setDistrict('');
  };

  const handleCantonChange = (e) => {
    const selectedCanton = e.target.value;
    setCanton(selectedCanton);
    // Reiniciar el valor de distrito cuando cambie el cantón
    setDistrict('');
  };

  const handleDistrictChange = (e) => {
    const selectedDistrict = e.target.value;
    setDistrict(selectedDistrict);
  };




  //--------------------------------------------------------------------------------------------------------------------------

  const classes = useStyles();

  const history = useHistory();

